var FormRepeater=function()
{
	return{
			init:function(){
				$(".mt-repeater").each(function(){
					$(this).repeater({
						show:function(){
							$(this).slideDown(),$(".date-picker").datepicker({rtl:App.isRTL(),orientation:"left",autoclose:!0})},
						hide:function(e){
							rid = $(this).find("a").attr("data-id");
							table = $(this).find("a").attr("data-table");
							var con = confirm("Are you sure you want to delete this element?");
							if(con)
							{
								if(rid!='' && rid!==undefined)
								{
									$.ajax({
										type:"POST",
										url:"http://162.144.41.156/~izaapinn/construction/admin/project/delete_dynamic",
										data:{id:rid,table:table},
										success:function(data)
										{
											// alert("Success");
										},
										error:function(data)
										{
											alert("Failed to delete");
										}
									});
								}
								$(this).slideUp(e);
							}
						},
						ready:function(e){
						}
					})
				})
			}
		}
	}();
jQuery(document).ready(function(){
	FormRepeater.init()
});